mapper.select:
select

from param_options
where ${filter}

  order by ${orderBy}

mapper.insert:
insert into param_options (id, createDate, createBy, 
  modifyDate, modifyBy, groupid, 
  `value`, showVal, sort
  )
values (#{id,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, #{createBy,jdbcType=INTEGER},
  #{modifyDate,jdbcType=TIMESTAMP}, #{modifyBy,jdbcType=INTEGER}, #{groupid,jdbcType=INTEGER},
  #{value,jdbcType=VARCHAR}, #{showVal,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}
  )
